    private void reverseBtnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_reverseBtnActionPerformed
        // TODO add your handling code here:
         String input = stringInput.getText();
         String reversedString = "";
       
          for (int i = input.length() - 1; i >= 0; i--) {
            reversedString += input.charAt(i);
        }
      outputArea.setText("Reversed String: " + reversedString);
    }//GEN-LAST:event_reverseBtnActionPerformed

    private void hexValBtnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_hexValBtnActionPerformed
        // TODO add your handling code here:
       String input = stringInput.getText();
         StringBuilder hexString = new StringBuilder(); 
   
       for (int i = 0; i < input.length(); i++) {
            char c = input.charAt(i);
            hexString.append(Integer.toHexString(c));
            hexString.append(" ");
        }
   outputArea.setText("The Hex value is: " + hexString.toString());
    }//GEN-LAST:event_hexValBtnActionPerformed

