    private void noWordBtnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_noWordBtnActionPerformed
        // TODO add your handling code here:
        String input = stringInput.getText();
        
        String[] words = input.split("\\s+");
        
        int wordCount = words.length;
        outputArea.setText("Number of Word: " + wordCount);
    }//GEN-LAST:event_noWordBtnActionPerformed

    private void noVowelBtnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_noVowelBtnActionPerformed
        // TODO add your handling code here:
        
        String input = stringInput.getText().toLowerCase();
        int vowelCount = 0;
        for (int i = 0; i < input.length(); i++) {
            char c = input.charAt(i);
            if (c == 'a' || c == 'e' || c == 'i' || c == 'o' || c == 'u') {
                vowelCount++;
            }
        }
        outputArea.setText("Number of Vowel: " + vowelCount);
    
    }//GEN-LAST:event_noVowelBtnActionPerformed

    private void noConBtnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_noConBtnActionPerformed
        // TODO add your handling code here:
        String input = stringInput.getText().toLowerCase();
        int consonantCount = 0;
        for (int i = 0; i < input.length(); i++) {
            char c = input.charAt(i);
            if (c >= 'a' && c <= 'z' && c != 'a' && c != 'e' && c != 'i' && c != 'o' && c != 'u') {
                consonantCount++;
            }
        }
      outputArea.setText("Number of Consonant: " + consonantCount);
    }//GEN-LAST:event_noConBtnActionPerformed

    private void lowerBtnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_lowerBtnActionPerformed
        // TODO add your handling code here:
        String input = stringInput.getText();
        String lowerString = input.toLowerCase();
        
        outputArea.setText("Lowercase Word: " + lowerString);
    }//GEN-LAST:event_lowerBtnActionPerformed

    private void jButton7ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton7ActionPerformed
        // TODO add your handling code here:
        String input = stringInput.getText();
        String upperString = input.toUpperCase();
        
        outputArea.setText("Uppercase string: " + upperString);
    
    }//GEN-LAST:event_jButton7ActionPerformed

    private void jButton8ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton8ActionPerformed
        // TODO add your handling code here:
        String input = stringInput.getText();
        
        String noSpacesString = input.replaceAll("\\s", "");
           outputArea.setText("Without spaces: " + noSpacesString);
    
    }//GEN-LAST:event_jButton8ActionPerformed

